$(function(){new Vue({el:"#app",data:{page:1,limit:15,count:0,items:[]},mounted:function(){this.load()},methods:{load:function(){let n=this,t=getPagedQuery();http.get(`/api/v1.0/agents?${t}`,function(t){n.page=t.data.page;n.limit=t.data.limit;n.count=t.data.count;n.items=[];t.data.data.forEach(t=>{n.items.push(t)})})},exit:function(n){swal({title:"Sure to stop this agent?",type:"warning",showCancelButton:!0},function(){let t=$(n.toElement).parent().parent().parent().attr("id");http.put("/api/v1.0/agents/"+t+"/exit",null,function(){swal("Success","Send signal success","success")},function(){swal("Error","Stop agent failed","error")})})},remove:function(n){const t=this;swal({title:"Sure to remove this agent?",type:"warning",showCancelButton:!0},function(){let i=$(n.toElement).parent().parent().parent().attr("id");http.delete("/api/v1.0/agents/"+i,function(){t.load()},function(){swal("Error","Remove agent failed","error")})})}}})});