$(function(){Vue.component("select2",{props:["options","value"],template:"#select2-template",mounted:function(){let n=this;$(this.$el).select2({data:this.options,minimumResultsForSearch:Infinity}).val(this.value).trigger("change").on("change",function(){n.$emit("input",this.value)})},watch:{value:function(n){$(this.$el).val(n).trigger("change")},options:function(n){$(this.$el).empty().select2({data:n})}},destroyed:function(){$(this.$el).off().select2("destroy")}})});